mapper.select:
SELECT COUNT(*) FROM MEMBER M JOIN POSITION P ON M.POSITION_NO = P.NO JOIN RANK R ON P.RANK_NO = R.NO JOIN DEPT D ON P.DEPT_NO = D.NO WHERE QUIT_YN IN('Y','N')
AND D.NO = #{selectDept}
AND P.NO = #{selectPosi}
AND M.NAME LIKE '%' || #{searchValue} || '%'

SELECT M.NO, M.NAME, M.EMAIL, M.PHONE, M.PROFILE_NAME , P.NAME AS POSITION_NAME , R.NAME AS RANK_NAME , D.NAME AS DEPT_NAME , M.GRADE , M.ADDRESS , M.ENROLL_DATE , M.UPDATE_DATE , M.START_DATE , M.QUIT_DATE , M.QUIT_YN , P.NO AS POSITON_NO , D.NO AS DEPT_NO FROM MEMBER M JOIN POSITION P ON M.POSITION_NO = P.NO JOIN RANK R ON P.RANK_NO = R.NO JOIN DEPT D ON P.DEPT_NO = D.NO WHERE M.QUIT_YN = 'N'
AND D.NO = #{upperDept}
ORDER BY P.NO ASC;

mapper.update:
UPDATE BOARD
SET DELETE_YN = 'Y'
WHERE NO = #{no}
