mapper.select:
SELECT COUNT(*) FROM MEMBER 
WHERE EMAIL = #{email} 
AND PRIVATE_EMAIL = #{privateEmail}
AND QUIT_YN = 'N'

SELECT COUNT(*) FROM MEMBER 
WHERE NO = #{no} 
AND PWD = #{pwd}
AND QUIT_YN = 'N'

mapper.update:
UPDATE MEMBER SET 
NAME = #{name}
,EMAIL = #{email}
,PRIVATE_EMAIL = #{privateEmail}
,GRADE = #{grade}
,ADDRESS = #{address}
,ADDRESS2 = #{address2}
,PHONE = #{phone}
,PROFILE_NAME = #{profileName}
,POSITION_NO = #{positionNo}
,BANK_NO = #{bankNo}
,ACCOUNT = #{account}
,UPDATE_DATE = SYSDATE
WHERE NO = #{no}

UPDATE MEMBER SET 
PWD = #{pwd}
WHERE NO = #{no}

UPDATE MEMBER SET 
QUIT_DATE = #{quitDate}
, QUIT_YN = 'Y'
WHERE NO = #{no}
