mapper.select:
SELECT * FROM ( SELECT B.*,K.NAME AS RANK_NAME, T.NAME DEPT_NAME FROM ( SELECT A.*, P.NAME AS POSITION, P.RANK_NO, P.DEPT_NO FROM ( SELECT Z.*, M.NO, M.NAME, M.EMAIL, M.ADDRESS, M.PHONE, M.PROFILE_NAME, M.BANK_NO, M.ACCOUNT, M.ENROLL_DATE, M.QUIT_YN FROM SALARY_BOOK Z JOIN MEMBER M ON Z.TARGET_NO = M.NO WHERE Z.PAY_DATE = #{payDate} ) A JOIN POSITION P ON A.NO = P.NO WHERE A.QUIT_YN = 'N' ) B JOIN RANK K ON B.RANK_NO = K.NO JOIN DEPT T ON B.DEPT_NO = T.NO ) C
WHERE C.DEPT_NO = #{deptNo}
AND C.RANK_NO = #{rankNo}
