mapper.select:
SELECT P.NAME AS POSITION_NAME ,P.RANK_NO ,D.NAME AS DEPT_NAME ,D.UPPER_DEPT ,D.LEV ,M.NO AS NO ,M.NAME AS NAME FROM POSITION P JOIN DEPT D ON P.DEPT_NO = D.NO JOIN MEMBER M ON M.POSITION_NO = P.NO
WHERE D.NO = #{deptNo}
ORDER BY M.NO ASC

mapper.insert:
INSERT INTO SIGN
(S_NO
, E_NO
, S_TYPE_NO
,S_TITLE
,S_CONTENT)

VALUES(SEQ_SIGN_NO.NEXTVAL
,#{eNo}
,#{sTypeNo}
,#{sTitle}
,#{sContent}
)

	SELECT SEQ_SIGN_NO.CURRVAL FROM DUAL

mapper.update:
UPDATE SIGN_LINE
SET
S_STEP ='Y'
WHERE S_NO = #{signNo}
AND E_NO = #{loginNo}

UPDATE SIGN_LINE
SET
S_STEP = 'X'
WHERE S_NO = #{signNo}
AND E_NO = #{loginNo}

mapper.select:
SELECT COUNT(S_NO)
FROM SIGN 
WHERE S_STATUS ='X' AND E_NO = #{memberNo}
