mapper.select:
SELECT P.NAME AS POSITION_NAME ,P.RANK_NO ,D.NAME AS DEPT_NAME ,D.UPPER_DEPT ,D.LEV ,M.NO AS NO ,M.NAME AS NAME FROM POSITION P JOIN DEPT D ON P.DEPT_NO = D.NO JOIN MEMBER M ON M.POSITION_NO = P.NO
WHERE D.NO = #{deptNo}
ORDER BY M.NO ASC

mapper.insert:
